import React from 'react';
import { createRoot } from 'react-dom/client';
import { Stage, Layer, Rect, Text } from 'react-konva';
import road_data from './dummy-streets.json';

function generate_road(nodes) {
  const roads = []
  for (const node of Object.entries(nodes)) {
    roads.push(node)
  }
  return {
    roads
  }
}

const CONNECTIONS = generate_road(road_data.nodes.connections);
const ATTRACTIONS = generate_road(road_data.nodes.attractions);

const App = () => {
  const roads = React.useState(CONNECTIONS);
  const buildings = React.useState(ATTRACTIONS)
    return (
      <Stage width={window.innerWidth} height={window.innerHeight}>
        <Layer>
          <Text text="Test Map" fontSize={15} />
          {roads[0].roads.map((_, i) => (
          <Rect
            key={i}
            id={roads[0].roads[i][0]}
            x={roads[0].roads[i][1].lon}
            y={roads[0].roads[i][1].lat}
            width={50}
            height={50}
            fill="red"
            shadowBlur={5}
          />
        ))}
        {buildings[0].buildings.map((_, i) => (
          <Rect
            key={i}
            id={buildings[0].buildings[i][0]}
            x={buildings[0].buildings[i][1].lon}
            y={buildings[0].buildings[i][1].lat}
            width={50}
            height={50}
            fill="red"
            shadowBlur={5}
          />
        ))}
        </Layer>
      </Stage>
    );
  };
  
  const container = document.getElementById('root');
  const root = createRoot(container);
  root.render(<App />);
