import React from 'react';
import { createRoot } from 'react-dom/client';
import { Stage, Layer, Rect, Text } from 'react-konva';
import road_data from './dummy-streets.json';

function generate_road(nodes) {
  const roads = []
  for (const node of Object.entries(nodes)) {
    roads.push(node)
  }
  return {
    roads
  }
}

const INITIAL_STATE = generate_road(road_data.nodes.connections);

const App = () => {
  const roads = React.useState(INITIAL_STATE);
  for (const road of Object.entries(roads)) {
    console.log(roads[0].roads[road[0]][0])
  }
  //roads.map((_, i) => (
    //console.log(roads[i].roads[i][0])
  //))
    return (
      <Stage width={window.innerWidth} height={window.innerHeight}>
        <Layer>
          <Text text="Test Map" fontSize={15} />
          {roads.map((_, i) => (
          <Rect
            key={i}
            id={roads[0].roads[i][0].toString()}
            //id={roads[i].roads[i][0].toString()}
            x={roads[0].roads[i][1].lon * 100}
            y={roads[0].roads[i][1].lat * 100}
            //x={roads[i].roads[i][1].lon}
            //y={roads[i].roads[i][1].lat}
            width={50}
            height={50}
            fill="red"
            shadowBlur={5}
          />
        ))}
        </Layer>
      </Stage>
    );
  };
  
  const container = document.getElementById('root');
  const root = createRoot(container);
  root.render(<App />);
