import React from 'react';
import { createRoot } from 'react-dom/client';
import { Stage, Layer, Rect, Text } from 'react-konva';
import road_data from './backend/salisbury-road-just-roads.json';

function nodes_to_array(nodes) {
  var roads = []
  for (const node of Object.entries(nodes)) {
    roads.push(node)
  }
  return {
    roads
  }
}

/*function way_points(nodes, way) {
  for (const node of Object.entries(way)) {
    if ()
  }
}*/

const GEN_ROADS = nodes_to_array(road_data.nodes.connections)
const GEN_ATTRS = nodes_to_array(road_data.nodes.attractions)
const GEN_WAYS = nodes_to_array(road_data.ways.roads)

const App = () => {
  const roads = React.useState(GEN_ROADS)
  const attrs = React.useState(GEN_ATTRS)
  const ways = React.useState(GEN_WAYS)
  for (let i = 0; i < ways[0].roads.length; i++) {
    console.log(ways[0].roads[i][1].name)
    //console.log(ways[0].roads[i][1].jsonObj["node-refs"])
  }
  const widthFactor = 1 / 2
  const heightFactor = 1 / 2
    return (
      <Stage width={window.innerWidth} height={window.innerHeight}>
        <Layer>
          <Text text="Test Map" fontSize={15} />
          {roads[0].roads.map((_, i) => (
          <Rect
            key={i}
            id={roads[0].roads[i][0]}
            x={roads[0].roads[i][1].lon * widthFactor}
            y={roads[0].roads[i][1].lat * heightFactor}
            width={5}
            height={5}
            fill="black"
          />
        ))}
          {attrs[0].roads.map((_, i) => (
          <Rect
            key={i}
            id={attrs[0].roads[i][0]}
            x={attrs[0].roads[i][1].lon * widthFactor}
            y={attrs[0].roads[i][1].lat * heightFactor}
            width={30}
            height={30}
            fill="red"
          />
        ))}
        </Layer>
      </Stage>
    );
  };
  
  const container = document.getElementById('root');
  const root = createRoot(container);
  root.render(<App />);
